import React from 'react'

const Header = (course) => {
    return (<>
        <h1>{course.name}</h1>
    </>)
}

const Part = (part) => {
    return (
        <div>
            <p>{part.name} {part.exerciseCount}</p>
        </div>
    )
}

const Content = (contents) => {
    return (
        <div>
            {contents.map((content, i) => <Part name={content.parts[i]} exerciseCount={content.exercises[i]} key={i} />)}
            </div>
    )
}

const Total = (total) => {
    let pointSum = 0;
    for (let i = 0; i < total.exerciseCounts.length; i++)
    {
        pointSum += total.exerciseCounts[i];
    }
    return (
        <p>Number of exercises {pointSum}</p>
    )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    'Fundamentals of React', 'Using props to pass data', 'State of a component'
  ]
  const exercises = [
      10, 7, 14
  ]

  return (
    <div>
      <Header name={course} />
      <Content parts={parts} exercises={exercises} />
      <Total exerciseCounts={parts} />
    </div>
  )
}

export default App
