import React from 'react'

const Header = (course) => {
    return (<>
        <h1>{course.name}</h1>
    </>)
}

const Content = (content) => {
    return (
        <p>{content.name} {content.exerciseCount}</p>
    )
}

const Total = (total) => {
    let pointSum = 0;
    for (let i = 0; i < total.exerciseCounts.length; i++)
    {
        pointSum += total.exerciseCounts[i];
    }
    return (

        <p>Number of exercises {pointSum}</p>
    )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header name={course} />
      <Content name={part1} exerciseCount={exercises1} />
      <Content name={part2} exerciseCount={exercises2} />
      <Content name={part3} exerciseCount={exercises3} />
      <Total exerciseCounts={[exercises1, exercises2, exercises3]} />
    </div>
  )
}

export default App
