import React, { useState } from 'react'

const Button = ({ text, onClick }) => {
    return (
        <>
            <button onClick={onClick}>{text}</button>
        </>
    )
}

const UserInput = ({ onGoodClicked, onNeutralClicked, onBadClicked }) => {
    return (
        <div>
            <h1>give feedback :)</h1>
            <Button onClick={onGoodClicked} text="good"/>
            <Button onClick={onNeutralClicked} text="good"/>
            <Button onClick={onBadClicked} text="good" />
        </div>
    )
}

const Statistics = ({ goodCount, neutralCount, badCount }) => {
    const all = goodCount + neutralCount + badCount;
    if (all == 0)
    {
        return (
            <div>
                <h1>statistics</h1>
                <p>No feedback given</p>
            </div>
        )
    }
    else
    {
        return (
            <div>
                <h1>statistics</h1>
                <p>good {goodCount}</p>
                <p>neutral {neutralCount}</p>
                <p>bad {badCount}</p>
                <p>all {all}</p>
                <p>average {all == 0 ? 0 : (goodCount - badCount) / all}</p>
                <p>positive {all == 0 ? 0 : goodCount / all * 100} %</p>
            </div>
        )
    }
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
        <UserInput onGoodClicked={() => setGood(good + 1)}
                    onNeutralClicked={() => setNeutral(neutral + 1)}
                    onBadClicked={() => setBad(bad + 1)} />
        <Statistics goodCount={good}
                    neutralCount={neutral}
                    badCount={bad} />
    </div>
  )
}

export default App
