import './App.css';
import { useEffect, useState } from 'react';
import axios from 'axios';

const FilterCountries = ({ filter, onFilterChanged }) => {
    return (
        <div>
            find countries <input value={filter} onChange={onFilterChanged} />
        </div>
    )
}

const FullCountry = ({ country }) => {

}

const Country = ({ country }) => {
    return (
        <div>
            {country.name}
        </div>
    )
}

const DisplayCoutnries = ({ countries, filter }) => {
    const displayedCountries = 
        filter === '' ? 
        countries : 
        countries.filter(country => country.name.toLowerCase().includes(filter.toLowerCase()));

    if (displayedCountries.length > 10)
    {
        return (
            <div>Too many matches, specify another filter!</div>
        )
    }
    else if (displayedCountries.length === 1)
    {
        return (
            <div>
                <FullCountry country={country} />
            </div>
        )
    }
    else
    {
        return (
            <div>
                {displayedCountries.map(country => <Country country={country} key={country.numericCode} />)}
            </div>
        )
    }
}

const App = () => {
    const [countries, setCountries] = useState([]);
    const [filter, setFilter] = useState('');

    useEffect(() => {
        axios.get("https://restcountries.eu/rest/v2/all").then(response => {
            setCountries(response.data);
        })
    }, []);

    function onFilterChanged(event) {
        setFilter(event.target.value);
    }

    return (
        <div>
            <FilterCountries filter={filter} onFilterChanged={onFilterChanged} />
            <DisplayCoutnries countries={countries} filter={filter} />
        </div>
    );
}

export default App;
