import React, { useState } from 'react'

const DisplayPerson = ({ person }) =>
{
    return (
        <div>
            {person.name}  {person.number}
        </div>
    )
}

const DisplayPhoneBook = ({ shownPeople }) => {
    return (
        <div>
            {shownPeople.map(person => <DisplayPerson person={person} key={person.id} />)}
        </div>
    )
}

const Filter = ({ filterName, setNewFilterName }) => {
    function onFilterNameChange(event)
    {
        setNewFilterName(event.target.value);
    }

    return (
        <div>
            filter names <input value={filterName} onChange={onFilterNameChange} />
        </div>
    )
}

const App = () => {
    const [persons, setPersons] = useState([
        {
            id: 1,
            name: 'Arto Hellas',
            number: '040-1234567'
        }
    ]);
    const [newName, setNewName] = useState('');
    const [newNumber, setNewNumber] = useState('');
    const [filterName, setNewFilterName] = useState('');

    function onAddName(event)
    {
        event.preventDefault();

        const person = {
            id: persons.length + 1,
            name: newName,
            number: newNumber
        };

        if (persons.find(p => p.name === person.name) !== undefined)
        {
            alert(`${person.name} is already added to phonebook!`);
        }
        else
        {
            setPersons(persons.concat(person));
        }

        setNewName('');
        setNewNumber('');
    }

    function onNameChange(event)
    {
        setNewName(event.target.value);
    }

    function onNumberChange(event)
    {
        setNewNumber(event.target.value);
    }

    const shownPeople = 
        filterName === '' ? 
        persons : 
        persons.filter(person => person.name.toLowerCase().includes(filterName.toLowerCase()));

    return (
        <div>
            <h2>Phonebook</h2>
            <form onSubmit={onAddName}>
                <div>
                    name: <input value={newName} onChange={onNameChange} />
                </div>
                <div>
                    number: <input type="tel" value={newNumber} onChange={onNumberChange} />
                </div>
                <div>
                    <button type="submit">add</button>
                </div>
            </form>

            <Filter filterName={filterName} setNewFilterName={setNewFilterName} />

            <h2>Numbers</h2>

            <DisplayPhoneBook shownPeople={shownPeople} />
        </div>
    )
}

export default App
