import React, { useState } from 'react'

const Number = ({ person }) =>
{
    return (
        <div>
            {person.name}  {person.number}
        </div>
    )
}

const App = () => {
    const [persons, setPersons] = useState([
        {
            id: 1,
            name: 'Arto Hellas',
            number: '040-1234567'
        }
    ]);
    const [newName, setNewName] = useState('');
    const [newNumber, setNewNumber] = useState('');
    const [filterName, setNewFilterName] = useState('');

    function onAddName(event)
    {
        event.preventDefault();

        const person = {
            id: persons.length + 1,
            name: newName,
            number: newNumber
        };

        if (persons.find(p => p.name === person.name) !== undefined)
        {
            alert(`${person.name} is already added to phonebook!`);
        }
        else
        {
            setPersons(persons.concat(person));
        }

        setNewName('');
        setNewNumber('');
    }

    function onNameChange(event)
    {
        setNewName(event.target.value);
    }

    function onNumberChange(event)
    {
        setNewNumber(event.target.value);
    }

    return (
        <div>
            <h2>Phonebook</h2>
            <form onSubmit={onAddName}>
                <div>
                    name: <input value={newName} onChange={onNameChange} />
                </div>
                <div>
                    number: <input type="tel" value={newNumber} onChange={onNumberChange} />
                </div>
                <div>
                    <button type="submit">add</button>
                </div>
            </form>
            <div>
                filter names <input value={filterName} onChange={onFilterNameChange} />
            </div>
            <h2>Numbers</h2>
            {persons.map(person => <Number person={person} key={person.id} />)}
        </div>
    )
}

export default App
